package share.exchange.framework.base;import android.os.Bundle;/** * * @ClassName:      BaseAppMVPFragment * @Description:    MVP 架构Fragment的基类 * @Author:         ZL * @CreateDate:     2019/08/05 14:30 */public abstract class BaseAppMVPFragment<P extends BaseMVP.MvpPresenter> extends BaseAppFragment implements BaseMVP.MvpView<P> {    private P mPresenter;    @Override    protected void initView(Bundle savedInstanceState) {        mPresenter = injectDependencies();        if (getPresenter() == null) {            throw new IllegalArgumentException("You must inject the dependencies before retrieving the presenter");        }        mPresenter.createView();    }    @Override    public void onDestroy() {        super.onDestroy();        mPresenter.destroyView();    }    @Override    public P getPresenter() {        return mPresenter;    }}